    display: flex;
    justify-content: space-between;
    padding: 2rem 5rem;
    max-width: 1300px;
    margin: 0 auto;
    align-items: center;
    span {
        img {
            display: flex;
        }
    }
    ul {
        display: flex;
        flex-wrap: wrap;
        gap: 1rem;
        list-style: none;
        justify-content: center;
        align-items: center;
        li a {
            line-height: 1.3;
            text-decoration: none;
            color: var(--color-header-menu);
            padding: 0.5rem 1rem;
            display: block;
        }
    }
}

@media (max-width: 800px) {
    header {
        flex-direction: column;
        gap: 3rem;
        ul li a {
            background-color: #b1b1b11c;
            border-radius: 0.5rem;
        }
    }
}

