main {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    align-items: center;
    max-width: 1300px;
    padding: 0 5rem;
    margin: 0 auto;
    height: calc(100vh - 8.5rem);
    .main-div-title {
        display: flex;
        flex-direction: column;
        gap: 2rem;
        h2 {
            font-size: 2.5rem;
            color: var(--color-title);
        }
        span {
            font-size: 1.2rem;
            color: #b1b1b1;
        }
        .main-div-btn {
            display: flex;
            gap: 1rem;
            .btn-download {
                display: flex;
                text-decoration: none;
                padding: 1rem 2rem;
                background-color: #A839FF;
                color: #fff;
                &:hover {
                    filter: brightness(80%);
                }
            }
            .btn-saibaMais {
                display: flex;
                text-decoration: none;
                padding: 1rem 2rem;
                font-size: 1rem;
                color: var(--color-header-btn-saiba);
                border: 1px solid var(--color-header-btn-saiba);
                &:hover {
                    filter: brightness(80%);
                    border-color: #A839FF;
                    color: #A839FF;
                }
            }
        }
    }
    img {
        width: max-content;
        height: fit-content;
    }
}

@media (max-width: 800px) {
    main {
        grid-template-columns: 1fr;
        grid-template-rows: repeat(2, 1fr);
        .main-div-title {
            justify-content: center;
            align-items: center;
            text-align: center;
        }
    }
}

@media (max-width: 500px) {
    main {
        padding: 0.5rem;
    }
}

sources.../src/styles/section.scss:
section {
    display: flex;
    flex-direction: column;
    padding: 2rem;
    max-width: 1300px;
    margin: 0 auto;
    align-items: center;
    gap: 8rem;
    justify-content: center;
    .section-sobre {
        display: flex;
        max-width: max-content;
        flex-direction: column;
        align-items: center;
        justify-self: center;
        gap: 1rem;
        h2 {
            font-size: 2.5rem;
            color: var(--color-title-sobre);
            font-weight: 500;
        }
        p {
            padding: 0 2rem;
            color: #B1B1B1;
            text-align: center;
            font-size: 1.5rem;
            max-width: 70ch;
            font-weight: 300;
            flex-wrap: wrap;
        }
        span {
            font-weight: bold;
            color: var(--color-sobre-span);
        }
    }
    .section-card-work {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        place-items: center;
        gap: 5rem;
        width: 100%;
        flex-wrap: wrap;
        .card-word {
            display: flex;
            align-items: flex-start;
            flex-direction: column;
            width: 100%;
            max-width: 15rem;
            padding: 2rem;
            gap: 1rem;
            position: relative;
            background-color: #212121;
            border-bottom: 4px solid transparent;
            img {
                height: 3rem;
            }
            h2 {
                font-weight: 500;
                color: #535353;
                font-size: 2rem;
                max-width: 5ch;
            }
        }
        .border-bottom-card {
            border-bottom: 4px solid #A620F0 !important;
        }
    }
    .section-contato {
        display: flex;
        ul {
            display: grid;
            flex: 1;
            grid-template-columns: repeat(4, 1fr);
            li {
                display: flex;
                align-items: center;
                justify-content: center;
                flex-direction: column;
                text-decoration: none;
                color: var(--color-title-contato);
                gap: 1rem;
                span {
                    font-size: 1.2rem;
                    font-weight: 500;
                }
                a {
                    text-align: center;
                    text-decoration: none;
                    color: var(--color-sub-contato);
                    padding: 0 1rem;
                    font-size: 1.2rem;
                }
                &::before {
                    content: '';
                    padding: 0.25rem;
                    background-color: #A620F0;
                    border: 1.35rem solid #212121;
                    border-radius: 50%;
                    display: flex;
                }
            }
        }
    }
    .section-experiencia {
        max-width: 1300px;
        padding: 40px 20px;
        margin: 0 auto;
        display: grid;
        grid-template-columns: 1fr 2fr;
        gap: 100px;
        .subtitulo-experiencia {
            color: var(--color-title-experiencia);
        }
        .experiencia-texto {
            font-size: 1.5rem;
            line-height: 1.33;
            max-width: 40ch;
            margin-bottom: 60px;
            color: #525252;
        }
        .experiencia-texto strong {
            color: #525252;
        }
        .empresa {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 10px 20px;
            padding: 20px;
            background: #525252;
            margin-bottom: 20px;
            border-radius: 4px;
            position: relative;
        }
        .empresa::before {
            content: "";
            display: block;
            width: 4px;
            height: 20px;
            background: linear-gradient(#b03af0, #9f00f5);
            position: absolute;
            top: 20px;
            left: -4px;
        }
        .empresa-ano {
            position: absolute;
            top: 22px;
            left: -100px;
            text-align: right;
            width: 80px;
            color: #525252;
            font-size: 0.875rem;
        }
        .empresa-titulo {
            font-size: 1.125rem;
            line-height: 1.1;
            font-weight: bold;
            color: #fff;
        }
        .empresa-texto {
            font-size: 0.875rem;
            line-height: 1.4;
            color: #CECDCD;
        }
        .empresa-habilidades {
            display: flex;
            flex-wrap: wrap;
            gap: 10px;
            align-content: start;
        }
        .empresa-habilidades li {
            font-size: 0.875rem;
            line-height: 1.4;
            border-radius: 4px;
            background: #A620F0;
            padding: 5px 10px;
            color: #fff;
        }
    }
}

@media (max-width: 1000px) {
    section {
        padding-top: 5rem;
        .section-card-work {
            display: flex;
            .card-word {
                display: flex;
                align-items: center;
                justify-content: center;
                max-width: 100%;
                width: 100%;
                h2 {
                    text-align: center;
                    display: flex;
                    justify-content: center;
                }
            }
        }
        .section-contato {
            align-items: center;
            ul {
                grid-template-columns: repeat(2, 1fr);
                gap: 2rem;
            }
        }
    }
}

@media (max-width: 900px) {
    section {
        .section-experiencia {
            grid-template-columns: 1fr;
            gap: 40px;
            .subtitulo-experiencia {
                text-align: center;
                font-size: 6rem;
                color: #717171;
                max-width: 100%;
            }
            .experiencia-texto {
                font-size: 1.25rem;
            }
            .empresa-ano {
                position: initial;
                order: 1;
                width: initial;
                text-align: left;
                color: #fff;
                font-weight: bold;
            }
        }
    }
}

@media (max-width: 800px) {
    .formacao-container {
        grid-template-columns: 1fr;
        padding: 60px 20px;
        gap: 40px;
    }
    .formacao-extra h3::before {
        position: initial;
        margin-bottom: 10px;
    }
}

@media (max-width: 700px) {
    section {
        gap: 3rem;
        .section-contato {
            align-items: center;
            ul {
                grid-template-columns: 1fr;
                gap: 2rem;
            }
        }
        .section-sobre {
            h2 {
                font-size: 1.5rem;
            }
        }
        .section-experiencia {
            .subtitulo-experiencia {
                text-align: center;
                font-size: 4rem;
            }
        }
    }
}

@media (max-width: 500px) {
    section {
        gap: 3rem;
        padding: 1rem;
        .empresa {
            grid-template-columns: 1fr;
        }
        .section-experiencia {
            .subtitulo-experiencia {
                text-align: center;
                font-size: 3rem;
            }
            .empresa-ano {
                color: #fff;
            }
        }
        .section-sobre p {
            padding: 0;
        }
    }
}

